import React, {useEffect, useState, useCallback, useMemo, useRef } from 'react'
import { useParams } from "react-router";
import axios from 'axios';
import { Baseurl } from './baseurl';
import QRCode from 'react-qr-code';
import useWebSocket, { ReadyState } from 'react-use-websocket';

const Payment = () => {
    let {code} = useParams();
    const [details, setDetails] = useState('')
    useEffect(() => {
        axios.post(`${Baseurl}payment.php`, JSON.stringify(code)
        ).then(res => {
         if(res.data){
        setDetails(res.data);
         }
        })}, [])
    
    const [socketUrl, setSocketUrl] = useState(`${Baseurl}status.php`);
    const messageHistory = useRef([]);
    const {
        sendMessage,
        lastMessage,
        readyState,
      } = useWebSocket(socketUrl);

    messageHistory.current = useMemo(() =>
    messageHistory.current.concat(lastMessage),[lastMessage]);
  
    const handleClickChangeSocketUrl = useCallback(() =>
      setSocketUrl('wss://demos.kaazing.com/echo'), []);
  
    const handleClickSendMessage = useCallback(() =>
      sendMessage('Hello'), []);
  
    const connectionStatus = {
      [ReadyState.CONNECTING]: 'Connecting',
      [ReadyState.OPEN]: 'Open',
      [ReadyState.CLOSING]: 'Closing',
      [ReadyState.CLOSED]: 'Closed',
      [ReadyState.UNINSTANTIATED]: 'Uninstantiated',
    }[readyState];
    return (
        <>
        <div className="container">
            <div className="row">
                <div className="col mt-5">
                    <h4 className="text-center">BTC PAYMENT</h4>
                {details ? <QRCode value={details.address} /> : null}
                  <p className="mt-5"><strong>Wallet Address: </strong>{details.address}</p>
                  <p><strong>Amount: </strong>{details.price}</p>
                  <p><strong>Status: </strong><span className="text-danger">{details.status}</span></p>
                  <p><strong>Payment for: </strong>{details.product}</p>
                  
                </div>

            </div>

        </div>

        
        </>
    )
}

export default Payment
